( function(timeout) {
	var init = function() {
		// http://stackoverflow.com/questions/15191058/css-rotation-cross-browser-with-jquery-animate
		jQuery.fn.animateRotate = function(angle, duration, easing, complete) {
			return this.each(function() {
				var $elem = jQuery(this);

				jQuery({deg: 0}).animate({deg: angle}, {
					duration: duration,
					easing: easing,
					step: function(now) {
						$elem.css({
							transform: 'rotate(' + now + 'deg)'
						});
					},
					complete: complete || jQuery.noop
				});
			});
		};
		
		jQuery( document ).keydown( function( event ) {
			if( event.which == 76 ) { // the "L" key
				unicorn();
			}
		} );
		
		window.setInterval( function() {
			unicorn();
		}, timeout );
	};
	
	// if our page doesn't have jquery load it in dynamically
	if( typeof jQuery === 'undefined' ) {
		var jq = document.createElement('script');
			jq.type = 'text/javascript'; 
			jq.src = '//ajax.googleapis.com/ajax/libs/jquery/3.2.1/jquery.min.js';
		
		var s = document.getElementsByTagName('script')[0]; 
			s.parentNode.insertBefore(jq, s);
		
		var intervalFunction = window.setInterval( function() {
			if( typeof jQuery !== 'undefined' ) { 
				window.clearInterval( intervalFunction );
				init();
			}
		}, 1000 );
	} else {
		init();
	}
	
	// random int min/max function
	function getRandomInt (min, max) {
		return Math.floor( Math.random() * ( max - min + 1 ) ) + min;
	}
	
	function unicorn() {
		var addTop = jQuery( window ).scrollTop(),
			width = 100,
			height = 67,
			imgProperties, img, anitime;
			
		switch( getRandomInt( 0, 2 ) ) {
			case 1:
				width /= 2;
				height /= 2;			
			break;
			case 2:
				width /= 5;
				height /= 5;			
			break;
		}
		
		imgProperties = {
			src64: 'data:image/png;base64,',
			leftStart: 0,
			leftEnd: jQuery( window ).width() - ( width * 1.1 ),
			top: getRandomInt( 0, jQuery( window ).height() - ( height * 1.1 ) )
		};
		
		if( getRandomInt( 0, 1 ) == 1 ) {
			imgProperties.src64 = 'data:image/png;base64,';
			imgProperties.leftStart = jQuery( window ).width() - width;
			imgProperties.leftEnd = 0;
		}
		
		img = jQuery( '<img src="' + imgProperties.src64 + '" height="' + height + '" width="' + width + '" />' );
		
		jQuery( 'body' ).append( img );
		
		anitime = getRandomInt( 1, 5 ) * 1000;
		
		img.css( {
			top: ( addTop + imgProperties.top ) + 'px',
			left: imgProperties.leftStart,
			position: 'absolute',
			'z-index': 9999999999
		} ).animateRotate( getRandomInt( 0, 359 ), anitime ).animate( {
			left: imgProperties.leftEnd
		}, anitime, function() {
			img.remove();
		} );
	}
}( 100 ) );